import React from 'react';
import Input from '../components/Input'
import { withTranslation  } from 'react-i18next';
import { login } from '../api/apiCalls';

class LoginPage extends React.Component{

    state = {
        username : null,
        password : null,
        error:null,
        pendingApiCall:null
    };

    onChange = event => {
        const {name,value}=event.target;
        const { t }=this.props;
        this.setState({
            [name] : value,
            error:null
        }
        );
    }

    onClickLogin = async event => {
        event.preventDefault();
        const { username, password } = this.state;
        const creds = {
          username,
          password
        };

        this.setState({
            error:null
        });

        try {
            await login(creds)
        } catch (apiError) {
            this.setState({
                error:apiError.response.data.message
            });
        }
      };

      componentDidMount




        render(){

            const {t}=this.props;
            const {username,password,error}=this.state;
            const buttonEnabled = username&&password;


        return(

            <div className="container ">
            <form>
                <div>
                <h1 className="text-center">{t('Login')}</h1>
                <Input name="username"  label ={t('Username')} onChange={this.onChange}  />
                <Input name="password"  label ={t('Password')} type="password" onChange={this.onChange}   />
                </div>
                {error && <div className="alert alert-danger">{error}</div>}
                <div className="text-center">
        <button  className="btn btn-primary" onClick={this.onClickLogin}  disabled={!buttonEnabled}      > 
        {t('Login')}</button>
                 </div>
            </form>


            </div>

          );
    }

}

export default withTranslation()(LoginPage);
