import React from 'react';
import axios from 'axios';
import {signup} from '../api/apiCalls' 
import Input from '../components/Input'

class UserSignupPage extends React.Component{    

    state = {
        username : null,
        displayName : null,
        password : null,
        Repeatpassword : null,
        pendingApiCall:false,
        errors: {}

    };

    onChange = event => {
        const {name,value}=event.target;  // object parçalama event targetin içindeki name ve value yi al anlamına geliyor
        const errors = { ...this.state.errors };
        errors[name]=undefined;

        if(name === 'password' || name === 'Repeatpassword'){
            if(name ==='password' && value!==this.state.Repeatpassword)
            errors.passwordRepeat ='Password Mismatch';
            else if (namehto   =='Repeatpassword' && value==this.state.password)
            errors.password ='Password Mismatch';
            else
            errors.passwordRepeat=undefined;
        }

        this.setState({
            [name] : value,
            errors
        });
    };

    onClickSignup = async event =>{

        event.preventDefault();

        const {username, displayName , password}=this.state;

        const body={
            username,
            displayName,
            password
        };

        this.setState(
           { pendingApiCall : true}
        );

        try {

            const response = await signup(body);

        } catch (error) {
            if (error.response.data.validationErrors) {
                    this.setState({ errors: error.response.data.validationErrors });
            }
                }


