import React from 'react';
import Input from '../components/Input'
import { withTranslation  } from 'react-i18next';
import {changeLanguage} from '../api/apiCalls' 

class LoginPage extends React.Component{

    state = {
        username : null,
        password : null,
    };

    onChange = event => {
        const {name,value}=event.target;
        const { t }=this.props;
        this.setState({
            [name] : value
        }
        );
    }

    onChangeLanguage = language => {

        const {i18n} = this.props;

        i18n.changeLanguage(language);
        changeLanguage(language);


    }



        render(){

            const {t}=this.props;

        return(

            <div className="container ">
            <form>
                <div>
                <h1 className="text-center">{t('Login')}</h1>
                <Input name="username"  label ={t('Username')} onChange={this.onChange}  />
                <Input name="password"  label ={t('Password')} type="password" onChange={this.onChange}   />
                </div>

                <div className="text-center">
        <button  className="btn btn-primary"  > {t('Login')}</button>
                 </div>
            </form>

            <div>    
    <img   src="https://www.countryflags.io/tr/flat/24.png" alt="Turkish Flag" onClick={() => this.onChangeLanguage('tr')} style = {{ cursor: 'pointer' }}></img> 
    <img src="https://www.countryflags.io/us/flat/24.png"  alt="English flag" onClick={() => this.onChangeLanguage('en')} style ={{ cursor: 'pointer' }} ></img>
</div>
            </div>

          );
    }

}

const UserSignupPageWithTranslation = withTranslation()(UserSignupPage);
export default UserSignupPageWithTranslation;
