import axios from 'axios';

class ApiProgress extends Component {
  state = {
    pendingApiCall: false
  };

    ccomponentDidMount(){

        axios.interceptors.request.use(request => {
            this.setState({
                pendingApiCall:true
            });
            return request;
        });

        axios.interceptors.response.use(
             response => {
            this.setState({pendingApiCall:false});
            return response;
             },
             error =>{         
                 this.setState({ pendingApiCall: false });
                 throw error;
